        /// <summary>
        /// Creates a frustum from camera parameters.
        /// </summary>
        /// <param name="position">Camera position.</param>
        /// <param name="forward">Camera forward direction (normalized).</param>
        /// <param name="up">Camera up direction (normalized).</param>
        /// <param name="fovY">Vertical field of view in radians.</param>
        /// <param name="aspect">Aspect ratio (width/height).</param>
        /// <param name="nearDist">Near plane distance.</param>
        /// <param name="farDist">Far plane distance.</param>
        /// <returns>The camera frustum.</returns>
        public static FrustrumDouble FromCamera(Double3 position, Double3 forward, Double3 up, 
                                               double fovY, double aspect, double nearDist, double farDist)
        {
            Double3 right = Maths.Normalize(Maths.Cross(forward, up));
            Double3 actualUp = Maths.Cross(right, forward);
            
            double halfFovY = fovY / 2.0;
            double tanHalfFovY = Maths.Tan(halfFovY);
            double tanHalfFovX = tanHalfFovY * aspect;
            
            // Calculate plane normals (pointing inward)
            Double3 nearCenter = position + forward * nearDist;
            Double3 farCenter = position + forward * farDist;
            
            // Near and far planes
            PlaneDouble near = new PlaneDouble(forward, Maths.Dot(forward, nearCenter));
            PlaneDouble far = new PlaneDouble(-forward, Maths.Dot(-forward, farCenter));
            
            // Side planes
            Double3 leftNormal = Maths.Normalize(Maths.Cross(actualUp, forward + right * tanHalfFovX));
            Double3 rightNormal = Maths.Normalize(Maths.Cross(forward - right * tanHalfFovX, actualUp));
            Double3 topNormal = Maths.Normalize(Maths.Cross(right, forward + actualUp * tanHalfFovY));
            Double3 bottomNormal = Maths.Normalize(Maths.Cross(forward - actualUp * tanHalfFovY, right));
            
            PlaneDouble left = new PlaneDouble(leftNormal, Maths.Dot(leftNormal, position));
            PlaneDouble right = new PlaneDouble(rightNormal, Maths.Dot(rightNormal, position));
            PlaneDouble top = new PlaneDouble(topNormal, Maths.Dot(topNormal, position));
            PlaneDouble bottom = new PlaneDouble(bottomNormal, Maths.Dot(bottomNormal, position));
            
            return new FrustrumDouble(near, far, left, right, top, bottom);
        }
        
