	[MethodImpl(MethodImplOptions.AggressiveInlining)]
	public static Byte2 Select(Bool2 mask, Byte2 trueValue, Byte2 falseValue)
	{
		return new Byte2(mask.X ? trueValue.X : falseValue.X, mask.Y ? trueValue.Y : falseValue.Y);
	}

